import { createSlice } from "@reduxjs/toolkit";
import audioService from "../services/audioList";

const audioSlice = createSlice({
	name: "audio",
	initialState: {
		all: [],
		current: null,
	},
	reducers: {
		setAudioList(state, action) {
			return { ...state, all: action.payload };
		},

		setCurrentAudio(state, action) {
			return { ...state, current: action.payload };
		},
	},
});

export const { setAudioList } = audioSlice.actions;

export const getAudioList = () => {
	return async (dispatch) => {
		const audioList = await audioService.getAll();
		dispatch(setAudioList(audioList));
	};
};

export const getCurrentAudio = (disptach) => {
	return async (audioName) => {
		disptach(setCurrentAudio(audioName));
	};
};

export default audioSlice.reducer;
