import { createSlice } from "@reduxjs/toolkit";
import audioService from "../services/audioList";

const audioSlice = createSlice({
	name: "audio",
	initialState: [],
	reducers: {
		setAudioList(state, action) {
			console.log("inside state:", state);
			return action.payload;
		},
	},
});

export const { setAudioList } = audioSlice.actions;

export const getAudioList = () => {
	return async (dispatch) => {
		const audioList = audioService.getAll();
		dispatch(setAudioList(audioList));
	};
};

export default audioSlice.reducer;
