import { createSlice } from "@reduxjs/toolkit";
import transcriptService from "../services/transcript";

const transcriptSlice = createSlice({
	name: "transcript",
	initialState: {
		all: [],
		current: null,
	},
	reducers: {
		setTranscriptst(state, action) {
			return { ...state, all: action.payload };
		},

		setCurrentTranscript(state, action) {
			return { ...state, current: action.payload };
		},
	},
});

export const { setTranscriptst, setCurrentTranscript } =
	transcriptSlice.actions;

export const setTranscriptList = () => {
	return async (dispatch) => {
		const audioList = await transcriptService.getAll();
		dispatch(setTranscriptst(audioList));
	};
};

export const setTranscript = (transcriptName) => {
	return async (dispatch, state) => {
		console.log("inside actions:", state);
		const transcript = await transcriptService.getAll();
		dispatch(setCurrentTranscript(transcript));
	};
};

export default transcriptSlice.reducer;
