import React, { useEffect, useRef } from "react";
import audioService from "./services/audioList";
import AudioList from "./components/AudioList";
import AudioPlayer from "./components/AudioPlayer";
import Transcript from "./components/Transcript";
import DropZone from "./components/DropZone";
import { getAudioList } from "./reducers/audioReducer";
import { useDispatch, useSelector } from "react-redux";
import { getTranscriptList } from "./reducers/transcriptReducer";

const App = () => {
	const audioPlayerRef = useRef(null);
	const dispatch = useDispatch();

	const transcriptName = useSelector(
		(state) => state.transcriptList.currentName
	);

	useEffect(() => {
		console.log(transcriptName);
	}, [transcriptName]);

	useEffect(() => {
		dispatch(getAudioList());
		dispatch(getTranscriptList());
	}, [dispatch]);

	return (
		<div>
			{console.log(audioPlayerRef)}
			<AudioPlayer ref={audioPlayerRef} />
			<Transcript audioPlayerRef={audioPlayerRef} />
			<AudioList />
			<DropZone />
		</div>
	);
};

export default App;
