import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { setCurrentAudio } from "../reducers/audioReducer";
import { setTranscriptName } from "../reducers/transcriptReducer";

const AudioList = () => {
	const dispatch = useDispatch();
	const audioList = useSelector((state) => state.audioList.all);

	const transcript = useSelector((state) => state.transcript);

	const handleClick = async (audioName) => {
		await dispatch(setCurrentAudio(audioName));
		await dispatch(setTranscriptName(audioName));
	};

	return (
		<ul>
			{audioList.map((audio) => (
				<li key={audio} onClick={() => handleClick(audio)}>
					{audio}
				</li>
			))}
		</ul>
	);
};

export default AudioList;
