import axios from "axios";

const baseUrl = "/api/audio";

const getAll = async () => {
	const request = await axios.get(baseUrl);
	return request.data;
};

const getAudio = async (id) => {
	const request = await axios.get(`${baseUrl}/${id}`);
	return request.data;
};

export default { getAll, getAudio };
