const formatTime = (seconds) => {
	let m = Math.floor(s / 60);
	m = m >= 10 ? m : "0" + m;
	s = Math.floor(s % 60);
	s = s >= 10 ? s : "0" + s;
	return m + ":" + s;
};

const formatTranscript = (transcript) => {
	return transcript.reduce((prev, curr) => {
		return prev.concat(curr.words);
	}, []);
};

export default { formatTime, formatTranscript };
